import{_ as a,c as e,o as r,U as t}from"./chunks/framework.FhXPyWfz.js";const m=JSON.parse('{"title":"积分管理","description":"","frontmatter":{"title":"积分管理","date":"2021-12-06T10:13:51.000Z"},"headers":[],"relativePath":"doc/wpdoc/积分管理.md","filePath":"doc/wpdoc/积分管理.md"}'),o={name:"doc/wpdoc/积分管理.md"},c=t('<h1 id="积分管理" tabindex="-1">积分管理 <a class="header-anchor" href="#积分管理" aria-label="Permalink to &quot;积分管理&quot;">​</a></h1><h3 id="一、业务价值主张" tabindex="-1">一、业务价值主张 <a class="header-anchor" href="#一、业务价值主张" aria-label="Permalink to &quot;一、业务价值主张&quot;">​</a></h3><p>规范化积分管理过程，做到有积分方案、有积分来源、有明细追溯、有报表反馈，业务有激励。</p><h3 id="二、业务用途" tabindex="-1">二、业务用途 <a class="header-anchor" href="#二、业务用途" aria-label="Permalink to &quot;二、业务用途&quot;">​</a></h3><p>1.为激励DSR人员进行陈列活动的打造或者常规陈列的执行，企业通过积分的方式进行考核激励。此组件支持设置积分方案的投放规则，包括方案的投放范围、生效日期、积分规则等，积分指标目前仅包括“陈列执行合格”。</p><p>2.根据积分规则及业务执行，统计人员的积分得分及排名情况，并进行门店陈列打造合格率的分析</p><h3 id="三、组件功能点" tabindex="-1">三、组件功能点 <a class="header-anchor" href="#三、组件功能点" aria-label="Permalink to &quot;三、组件功能点&quot;">​</a></h3><p>（1）AI识别标准新增<br> （2）AI识别标准导入<br> （3）积分方案新增<br> （4）客户拜访<br> （5）常规生动化执行<br> （6）方案执行<br> （7）陈列活动执行<br> （8）AI结果定时调度识别<br> （9）积分定时计算<br> （10）月度积分定时计算<br> （11）积分明细报表<br> （12）积分排名(日/月)报表<br> （13）业绩达标建设统计(日)<br> （14）业绩达标建设统计(月)<br> （15）门店达标建设统计<br> （16）门店AI产品识别结果统计</p><h3 id="四、业务流程图" tabindex="-1">四、业务流程图 <a class="header-anchor" href="#四、业务流程图" aria-label="Permalink to &quot;四、业务流程图&quot;">​</a></h3><p><img src="http://apaas.wxchina.com:8881/wp-content/uploads/image-20211202114350045.png" alt=""></p><p>相关文件下载：<br><a href="http://apaas.wxchina.com:8881/wp-content/uploads/SP-YL-BC417-%E7%A7%AF%E5%88%86%E7%AE%A1%E7%90%86.zip" title="SP-YL-BC417 积分管理" target="_blank" rel="noreferrer">SP-YL-BC417 积分管理</a></p>',11),n=[c];function i(d,h,l,p,s,_){return r(),e("div",null,n)}const u=a(o,[["render",i]]);export{m as __pageData,u as default};
